import '../styles/Registration.css';
import { Link } from "react-router-dom";
import { Navbar } from '../components/Navbar';
import { Footer } from '../components/Footer';
import { useNavigate } from "react-router-dom";
import bann1 from '../images/img1..jpeg';
import React, { useState, useEffect } from "react";
import axios from "axios";

const SignUp = () => {
    const [users, setUsers] = useState([]);
    const [user, setUser] = useState({ id: "", name: "", email: "" });
    const [editing, setEditing] = useState(false);
    const [error, setError] = useState(""); // Error state

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const response = await axios.get(`http://localhost:9090/api/users`);
            setUsers(response.data);
            setError(""); // Clear error on success
        } catch (error) {
            setError("Failed to fetch users. Please try again later.");
            console.error("Fetch Error: ", error);
        }
    };


    const handleChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!user.name || !user.email) {
            setError("Name and Email are required!");
            return;
        }

        try {
            if (editing) {
                await axios.put(`http://localhost:9090/api/users/${user.id}`, user);
            } else {
                await axios.post(`http://localhost:9090/api/users`, user);
            }
        
            setUser({ id: "", name: "", email: "" });
            setEditing(false);
            setError("");
            fetchUsers();
        } catch (error) {
            setError("Failed to save user. Please try again.");
            console.error("Save Error:", error);
        } 
    };

    const editUser = (user) => {
        setUser(user);
        setEditing(true);
        setError("");
    };
    
    const deleteUser = async (id) => {
        if (!window.confirm("Are you sure you want to delete this user?")) {
            return;
        }
    
        try {
            await axios.delete(`http://localhost:9090/api/users/${id}`);
            fetchUsers();
        } catch (error) {
            setError("Failed to delete user. Please try again.");
            console.error("Delete Error:", error);
        }
    };

    return (
        <div className="signup">
            <Navbar />
            <div className="signup_body">
                <div className="signup_body_leftside">
                    <h1>Sign up to continue<br/>your learning journey</h1>
                        <form className="signup_form1">
                            <input type="text" placeholder='Full Name' required />
                            <input type="email" placeholder='Email' required />
                        </form>
                        <form className="signup_form2">
                            <input type="password" placeholder='Password' required />
                            <input type="password" placeholder='Confirmed Password' required />
                        </form>
                        <Link to = "/dashboard" className='signup_link'>Create Account</Link>
                    <p>By signing up, you agree to our <Link to="">Terms of Use</Link> and <Link to="">Privacy Policy.</Link></p>
                    <p>Already have an account? <Link to="/login">Login</Link></p>
                </div>
                <img src={bann1} alt="study" />
            </div>
            <Footer />
        </div>
    )
}

export default SignUp;
