import '../styles/Registration.css';
import { Link, useNavigate } from "react-router-dom";
import { Navbar } from '../components/Navbar';
import { Footer } from '../components/Footer';
import bann1 from '../images/img1.jpeg';
import React, { useState } from "react";
import axios from "axios";

const SignUp = () => {
    const navigate = useNavigate();
    const [user, setUser] = useState({ name: "", email: "", password: "" });
    const [error, setError] = useState("");

    const handleChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(""); // Clear previous errors

        if (!user.name || !user.email || !user.password) {
            setError("⚠️ All fields are required!");
            return;
        }

        try {
            console.log("Sending data to backend:", user);
            const response = await axios.post(`http://localhost:9090/api/users`, user);
            
            console.log("Signup Response:", response.data);
            if (response.status === 201 || response.status === 200) {
                alert("✅ Account Created Successfully!");
                setUser({ name: "", email: "", password: "" });
                navigate("/dashboard"); // Redirect after success
            } else {
                setError("❌ Signup failed! Please try again.");
            }
        } catch (error) {
            console.error("Signup Error:", error.response?.data || error.message);
            setError("❌ Unable to sign up. Check your details and try again.");
        }
    };

    return (
        <div className="signup">
            <Navbar />
            <div className="signup_body">
                <div className="signup_body_leftside">
                    <h1>Sign up to continue<br/>your learning journey</h1>
                    
                    {error && <p className="error">{error}</p>} {/* Show errors */}

                    <form className="signup_form1" onSubmit={handleSubmit}>
                        <input 
                            type="text" 
                            name="name" 
                            value={user.name} 
                            placeholder="Enter Full Name" 
                            onChange={handleChange} 
                            required 
                        />
                        <input 
                            type="email" 
                            name="email" 
                            value={user.email} 
                            placeholder="Enter Email" 
                            onChange={handleChange} 
                            required 
                        />
                        <input 
                            type="password" 
                            name="password" 
                            value={user.password} 
                            placeholder="Create Password" 
                            onChange={handleChange} 
                            required 
                        />
                        <button className='signup_link' type="submit">
                            Create Account
                        </button>
                    </form>
